#include <iostream>
#include <vector>
#include <climits>
#include <cmath>
using namespace std;

class Solution {
public:
	int superPow(int a, vector<int>& b) {
		return step(a, b);
	}

	// N is big number
	// M is 1337
	// B is 1000*1000*1000, 10^9
	// N = N0 + B*N1
	// (a^N)%M = (a^(N0+B*N1))%M = (((a^N0)%M)*(a^(B*N1))%M)%M
	// so, below is its code.
	int step(int a, vector<int>& b){
		int digits = 9;
		int b1 = getLastDigits(b);
		int b2 = pow(10, digits);

		if(b1 == 0)
			return 1;

		int result1 = do_step(a, b1);
		int result2 = do_step(a, b2);
		int result3 = step(result2, b);

		return (result1*result3) % 1337;
	}

	// return value in [0, pow(10, 9)).
	// get last 9 digits.
	int getLastDigits(vector<int>& b){
		int digits = 9; // get first 9 digits.
		int get = 0;
		for(int i=0; i<digits && i<b.size(); i++){
			get += b[b.size()-1-i] * pow(10, i);
		}
		while(b.size() > 0 && digits-- > 0){
			b.erase(b.begin() + b.size() - 1);
		}
		return get;
	}

	// a in [0, INT_MAX], n in [0, INT_MAX].
	// return value in [0, 1337).
	int do_step(int a, int n){
		int res = 1;
		a %= 1337;
		while(n>0){
			int td = a;
			unsigned int i = 2;
			for(; i<n; i*=2){
				td *= td;
				td %= 1337;
			}
			n = n - i/2;
//			cout << hex << n << endl;
			res *= td;
			res %= 1337;
		}
		return res % 1337;
	}
};

